const url=`https://api.github.com/repos/${ctx.match[1]}/${ctx.match[2]}/issues/${ctx.match[3]}`
const response = await fetch(url);
const data = await response.json();
const title = data.title;
return `[${ctx.match[2]}#${ctx.match[3]}: ${title}](${ctx.foundText})`;

const url=`https://api.github.com/repos/${ctx.match[1]}/${ctx.match[2]}/pulls/${ctx.match[3]}`
const response = await fetch(url);
const data = await response.json();
const title = data.title;
return `[${ctx.match[2]}#${ctx.match[3]}: ${title}](${ctx.foundText})`;
